element.calculation:
(function calculatedFieldValue(current) {
	var totalQuestionCount = parseInt(current.getValue('number_of_questions') || 0);
	var levelOneCount = parseInt(current.getValue('level_1_count') || 0);
	if(levelOneCount > 0 && totalQuestionCount > 0) return (levelOneCount/totalQuestionCount) * 100; 
	else return 0;
})(current);

(function calculatedFieldValue(current) {
    var totalQuestionCount = parseInt(current.getValue('number_of_questions') || 0);
    var levelTwoCount = parseInt(current.getValue('level_2_count') || 0);
    if (levelTwoCount > 0 && totalQuestionCount > 0) return (levelTwoCount / totalQuestionCount) * 100;
    else return 0;
})(current);

(function calculatedFieldValue(current) {
    var totalQuestionCount = parseInt(current.getValue('number_of_questions') || 0);
    var levelThreeCount = parseInt(current.getValue('level_3_count') || 0);
    if (levelThreeCount > 0 && totalQuestionCount > 0) return (levelThreeCount / totalQuestionCount) * 100;
    else return 0;
})(current);

(function calculatedFieldValue(current) {
    var totalQuestionCount = parseInt(current.getValue('number_of_questions') || 0);
    var levelFourCount = parseInt(current.getValue('level_4_count') || 0);
    if (levelFourCount > 0 && totalQuestionCount > 0) return (levelFourCount / totalQuestionCount) * 100;
    else return 0;
})(current);

(function calculatedFieldValue(current) {
	var numberOfQuestions = 0;
	var questions = current.getValue('questions');
	if(questions !== null) {
		numberOfQuestions = questions.split(',').length;
	}
	return numberOfQuestions;
})(current);
