sp_widget.client_script:
api.controller = function($scope, $rootScope, spModal, spUtil) {
    var c = this;
	spUtil.recordWatch($scope,"x_93700_arena_dyk_feedback","userDYNAMIC90d1921e5f510100a9ad2572f2b477fe^type=bookmark",function(res){
		$scope.server.refresh();
	});

    $scope.change = function(record) {
        var qid = record;
        if (!qid) return;
        $rootScope.$broadcast('loadContent', {
            'qid': qid
        });
    };

};

