sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.saved = [];
    var rec = new GlideRecord('x_93700_arena_dyk_feedback');
    rec.addEncodedQuery('type=bookmark^userDYNAMIC90d1921e5f510100a9ad2572f2b477fe^u_active=true');
    rec.query();
    while (rec.next()) {
        var articleName = rec.dyk_content.title;
        var articleTitle = articleName.length > 16 ? articleName.substr(0, 16) + "..." : articleName;

        data.saved.push({
            'article': articleTitle.toString(),
            'value': rec.dyk_content.toString()
        });
    }
  })();
