sp_widget.client_script:
function arenaIntro2() {
    /* widget controller */
    var c = this;


    /////////////////////////Listener/////////////////////////
    $rootScope.$on('introSequence2', function(event, obj) {
        c.showIntroSequence2 = obj;



        $("#welcomeText").delay(1000).fadeIn(7000, function cinematics(){

            $("#welcomeText").hide();
            c.showIntroSequence2 = false;

            var gameScreen = {
                gameHeading: true,
                mainMenu: true,
                gameLevels: false,
                quizPage: false,
                profilePage: false,
                leaderBoard: false,
            };

            $rootScope.$broadcast('gameScreen', gameScreen);
        });
    });


}

sp_widget.css:
section{
  display:flex;
  flex-direction:column;
  justify-content:center;
  align-items:center;
  text-align:center;
}
