sp_widget.client_script:
api.controller = function($scope, $sce) {
	var c = this;
	c.postAnswer = function() {
		if (!c.userInput) {
			M.Toast.dismissAll();
			M.toast({
				html: 'Please enter your suggestion here....',
				classes: 'rounded',
				timeRemaining: 5000
			});
			return;
		}
		c.data.action = "userInput";
		c.data.userInput = c.userInput;
		c.server.update().then(function() {
			c.userInput = "";
			M.toast({
				html: 'Thank You for your suggestion!!!',
				classes: 'rounded',
				timeRemaining: 5000
			});
		});
	}
};

sp_widget.css:
.card{
