  color: #FFF;
  font-weight: bold;
}
.pull-right{
  background: #2da2b1; /*Dark Cyan*/
  float:right; 
}

sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    if (input) {
        if (input.action === "userInput") {
            suggestion = new GlideRecord('x_93700_arena_dyk_suggestions');
            suggestion.initialize();
            suggestion.setValue('suggestion', input.userInput);
            suggestion.setValue('user', gs.getUserID());
            suggestion.insert();
        }
    }
})();
