sp_widget.client_script:
function($rootScope) {
	/* widget controller */
	var c = this;

	$rootScope.$on('gameScreen', function(event,obj) {
		c.showCredits = obj.showCredits;
	});

	var swiper = new Swiper('.swiper-container', {
		effect: 'coverflow',
		grabCursor: true,
		centeredSlides: true,
		slidesPerView: 'auto',
		spaceBetween: 10,
		mousewheel: true,
		keyboard: {
			enabled: true
		},
		autoplay: {
		    delay: 1500,
		    disableOnInteraction: true
		},
		coverflowEffect: {
			rotate: 30,
			stretch: 0,
			depth: 100,
			modifier: 1,
			slideShadows: true
		},
		navigation: {
			nextEl: '.swiper-button-next',
			prevEl: '.swiper-button-prev'
		}
	});
}

sp_widget.css:
