sp_widget.client_script:
api.controller=function($scope, $timeout) {
	/* widget controller */
	var c = this;

	// controls links visibility
	$scope.showLinks = true;

	// link cards
	$scope.links = [
		{
			label:'Arena Play', 
			href:'arena?id=arena_home', 
			className: 'play'
		},
		{
			label:'Arena Test Mode',
			href:'arena_test', 
			className: 'test'
		},
		{
			label:'Shankaran Pillai', 
			href:'arena?id=sp', 
			className: 'spillai'
		},
		{
			label:'Did you know', 
			href:'arena?id=dyk', 
			className: 'dyk'
		}
	];

	// default bg on page load
	$scope.bgClass = "default";

	$scope.handleMouseEvt = function(className) {
		// don't transition once link is clicked
		if ($scope.showLinks) {
			// add bgclass on mouseenter/remove on mouseleave
			$scope.bgClass = className ? className + ' blur' : "";
			// add default bg on mouseleave post debounce duration
			if (!className) {
				$timeout(function() {
					if (!$scope.bgClass) $scope.bgClass = 'default';
				}, 500);
			}
		}
	};

	// hide all link cards
	$scope.hideLinks = function() {
		$scope.showLinks = false;
	};

};

