sp_widget.client_script:
function(spModal) {

	var c = this;
	c.inHome = true;
	c.inQuizPage = false;



	c.process = function() {

		$rootScope.$broadcast('gameScreen', c.gameScreen);
	}


	c.goHome = function() {
		sfx.playTik();
		c.gameScreen = {
			introSequence: false,
			gameHeading: true,
			mainMenu: true,
			gameLevels: false,
			quizPage: false,
			profilePage: false
		};


		if (c.inQuizPage) {
			Notiflix.Confirm.Init({
				backOverlay: false
			});
			Notiflix.Confirm.Show(c.data.confirm_exit_heading, c.data.confirm_exit_message, 'Yes', 'No',               function() {
				c.process(console.log("yes"));
				$rootScope.$broadcast('truncateTest');
			},
			function() {
				console.log("No")
			});

		} else {
			c.process();
		}



	}

	$rootScope.$on('gameScreen', function(event, obj) {
		c.inHome = obj.mainMenu;
		c.inQuizPage = obj.quizPage;
	});
}

sp_widget.css:
.hover-bottom {

    position: fixed;
    top: 0;
    left: 0;
    border-radius: 50%;
    border-width: 0px;
    width: 50px;
    height: 50px;
    margin-left: 20px;
    margin-top: 40px;
    z-index: 999;
    font-size: 26px;
    font-weight: thin;

}

.hover-bottom:focus {outline:0;}

sp_widget.script:
(function() {

    var player = new GlideRecord("x_93700_arena_user");
    player.addQuery("user", gs.getUserID());
    player.addQuery('interviewee', true);
    player.query();
    if (player.next()) {
        data.interview = 'true';
    }


    data.confirm_exit_heading = gs.getMessage("Confirm Exit");
    data.confirm_exit_message = gs.getMessage("Are you sure?");
})();
