sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;


	var radialObj = radialIndicator('#indicatorContainer', {
    radius: 60,
    barWidth: 22,
    barColor: '#FF0000',
    minValue: 0,
    maxValue: 60,
    fontWeight: 'normal',
    roundCorner: true,
		fontColor:"green",
    format: function (value) {
        var date = new Date();
        return date.getHours() + ':' + date.getMinutes();
    }
});

	var time = 100;

setInterval(function () {
    radialObj.value(time);
	time = time-1;

	if(time==0)time=100;



}, 30);
}

sp_widget.css:
#indicatorContainerWrap,#indicatorContainer{
    display:inline-block;
    position:relative;
}
#prgLogo{
    position:absolute;
    width:60px;
    height:60px;
    margin-top:-30px;
    margin-left:-30px;
}
