sp_widget.client_script:
api.controller = function($scope, $sce) {
	/* widget controller */
	var c = this;

	$scope.tags = [];

	var autocompleteElement = angular.element('#dyk-search-autocomplete');
	var inputElement = angular.element('#search-tags');

	$scope.addTag = function(tag) {
		autocompleteElement.css({
			display: '',
			opacity: ''
		});
		$scope.tags.push(tag);
		$scope.dykSearchText = '';
		applyFilterFromTag();
	};

	$scope.removeTag = function(index) {
		$scope.tags.splice(index, 1);
		applyFilterFromTag();
	};

	function applyFilterFromTag() {
		$scope.data.action = 'search';
		$scope.data.filter = $scope.tags.map(function(tag) {
			return 'sys_tags' + tag.sys_id + '=' + tag.sys_id;
		}).join('^OR');
		$scope.server.update().then(function() {
			$scope.data.action = undefined;
			$scope.data.filter = undefined;
		});
	}

	$scope.showAutocomplete = function() {
		autocompleteElement.css({
			top: inputElement.outerHeight(),
			width: inputElement.outerWidth(),
			display: 'block',
			opacity: '1'
		});
	}

	$scope.filterOptions = function(tag, searchText) {
		return $scope.tags.indexOf(tag) === -1 && tag.label.toLowerCase().startsWith(searchText.toLowerCase());
	};

	$scope.getAutoCompletedEntry = function(label, searchText) {
		var searchTextLen = searchText.length;
		var indexOfSearchText = label.toLowerCase().indexOf(searchText.toLowerCase());
		return $sce.trustAsHtml('<span class="highlight">'+label.substr(0, indexOfSearchText + searchTextLen)+'</span>' + label.substring(indexOfSearchText + searchTextLen, label.length));
	};

};

sp_widget.css:
.chips-wrapper {
  display: flex;
  flow-flow: row;
  align-items: center;
  .dyk-search-input-wrapper {
    margin: 0 0 0 10px;
  }
}

sp_widget.script:
(function() {

	if (input && input.action === 'search') {
		gs.addInfoMessage(input.filter);
		return;
	}

	data.tagsAutocomplete = [];

	var tagGr = new GlideAggregate('label_entry');
	tagGr.addQuery('label.active', true);
	tagGr.addQuery('table', 'x_93700_arena_did_you_know');
	tagGr.groupBy('label');
	tagGr.query();
	while (tagGr.next()) {
		data.tagsAutocomplete.push({
			label: tagGr.label.getDisplayValue(),
			sys_id: tagGr.getValue('label')
		});
	}

})();
