  padding: 0 5px 5px 5px;
  height: 40vh;
  overflow-y: auto;
  overflow-x: hidden;
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.leaderboard=[];
	var ag = new GlideAggregate('x_93700_arena_answer');
	ag.addQuery('correct', true);
	ag.addAggregate('COUNT', 'user');
	ag.query();
	while(ag.next()) {
		data.leaderboard.push({
			'name':ag.user.getDisplayValue(),
			'score':ag.getAggregate('COUNT', 'user')
		});
	}
	data.leaderboard = data.leaderboard.sort(function(a,b){
		return b.score-a.score;
	});
})();
