sp_widget.client_script:
function($rootScope, $window) {
	/* widget controller */
	var c = this;
	c.showIcon = true;

	c.goToSp = function() {
		$window.location.href = '/arena?id=sp';
	}

	$rootScope.$on('gameScreen', function(event, screens) {
		if (screens.introSequence === true || screens.mainMenu === true) c.showIcon = true;
		else c.showIcon = false;
	});
}

sp_widget.css:
.icon-wrapper {
  position: fixed;
  height: 100px;
  background-image: url(shankaran-pillai-thumbnail.jpg);
  background-size: cover;
  border-radius: 50%;
  width: 100px;
  bottom: 1vh;
  right: 1vh;
  border: 2px solid grey;
  box-shadow: 1px 2px 5px #ccc;
}

.bounce {
  animation: bounce 0.75s infinite;
  -webkit-animation: bounce 0.75s infinite;
}

@-webkit-keyframes bounce {
  0% {
    transform: scale(1,1) translate(0px, 0px);
  }
  30%{
    transform: scale(1,0.8) translate(0px, 10px); 
  }
  75%{
    transform: scale(1,1.1) translate(0px, -25px); 
  }
  100% {
    transform: scale(1,1) translate(0px, 0px);
  }
}
