sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (current.value.changesTo(true)) {
        var tempQuesIdList = [];
        var question = new GlideRecord('x_93700_arena_arena_question');
        question.addQuery('active', true);
        question.query();
        while (question.next()) {
            tempQuesIdList.push(question.sys_id + '');
        }
        var randomQuestions = randomiser(tempQuesIdList, 10, tempQuesIdList.length);

        var setpropert = new GlideRecord('sys_properties');
        setpropert.addQuery('name', 'x_93700_arena.Hire_mode_question_sys_id');
        setpropert.query();
        if (setpropert.next()) {
            setpropert.value = randomQuestions.toString();
            setpropert.update();
        }





    } else {
        var setpropert = new GlideRecord('sys_properties');
        setpropert.addQuery('name', 'x_93700_arena.Hire_mode_question_sys_id');
        setpropert.query();
        if (setpropert.next()) {
            setpropert.value = '';
            setpropert.update();
        }

    }


    function randomiser(queArray, requiredQuestionCount) {
        var list = [];
        while (list.length < requiredQuestionCount) {
            var question = queArray[Math.floor(Math.random() * Math.floor(queArray.length))];
            if (list.indexOf(question) == -1)
                list.push(question);
        }
        return list;
    }


})(current, previous);
