sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here


	var rankArray=[];
	var players = new GlideAggregate('x_93700_arena_results');
	players.groupBy('arena_user');
	players.query();
	while(players.next())
	{
		var userStats = new GlideRecord('x_93700_arena_results');
		userStats.addQuery('arena_user',players.arena_user);
		userStats.addQuery('active','true');
		userStats.orderBy('level');
		userStats.query();
		var lastLevel = "";
		var levelsCleared=0;
		var totalScore=0;
		var totalTime=0;
		while(userStats.next())
		{
			totalScore += userStats.getValue('total_score');
			totalTime += userStats.getValue('time_taken');
			if(lastLevel!=userStats.getValue('level').toString())
				levelsCleared++;
			lastLevel = userStats.getValue('level').toString();
		}
		var playerSys = players.arena_user.sys_id;
		var p1 = new GlideRecord('sys_attachment');
		p1.addQuery('table_sys_id',playerSys);
		p1.query();
		if(p1.next())
			var image = "/" + p1.sys_id + ".iix";
		var gameName = players.arena_user.avatar.toString();
		var userSys = players.arena_user.user.toString();
		rankArray.push({
			'lastLevel':lastLevel,
			'levelsCleared':levelsCleared,
			'totalScore':totalScore,
			'totalTime':totalTime,
			'playerSys':playerSys,
			'image':image,
			'gameName':gameName,
			'userSys':userSys
		});
	}











})(current, previous);
