sys_script_fix.script:
var que = new GlideRecord('x_93700_arena_arena_question');
//que.setLimit(10);
//que.get('f2ea5019db0c1010b6db8e474896190e');
que.query();

var results_count = [];
while (que.next()) {
    var result = new GlideRecord("x_93700_arena_quiz_choices");
    result.addQuery("question", que.sys_id.toString());
    result.addQuery('correct', true);
    result.query();

    que.type = 'checkBox';

    if (result.getRowCount() == 1)
        que.type = 'radioButton';

    que.update();
}
