sys_script_fix.script:
var arenaQuestions = 100;


var randomNumbers = [];//A random list of unique numbers within limit of the number of questions
while(randomNumbers.length < 10){
	var questionNumber = Math.floor(Math.random() * arenaQuestions) + 1;
	if(randomNumbers.indexOf(questionNumber) === -1)
		randomNumbers.push(questionNumber);
}


var questionsLen = randomNumbers.length;
var questionsArr = [];

for(var i=0; i<questionsLen; i++)
	{
	var inc = new GlideRecord("incident");
	inc.setLimit(1);
	inc.chooseWindow(randomNumbers[i], randomNumbers[i] + 1);//Get the question stored at the random position  
	inc.query();
	if (inc.next()) {
		questionsArr.push(inc.number.toString());
	}
}

gs.info('The random incidents are - ' + questionsArr);
