sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

    var certificateNameChoiceVal = 'cis_grc';

    var gr = new GlideRecord('x_93700_arena_arena_question');
    gr.setLimit(1);
    gr.addEncodedQuery('module=certification^sub_module=' +
        certificateNameChoiceVal +
        '^questionLIKE' +
        source.u_questions.toString().split('\n')[0]);
    gr.query();
    if (gr.hasNext()) {
        ignore = true;
    }

    target.sub_module = certificateNameChoiceVal;

})(source, target, map, log, action === "update");
