sys_ui_action.script:
var questions = [],
    questionGr;
if (current.select_randomly == true) {
    questionGr = new GlideRecord('x_93700_arena_arena_question');
    questionGr.setLimit(parseInt(current.getValue('total_count') || 0));
    questionGr.addActiveQuery();
    questionGr.addEncodedQuery(current.filter);
    questionGr.query();
    while (questionGr.next()) {
        questions.push(questionGr.getValue('sys_id'));
    }
    current.setValue('total_count', questions.length);
} else {
    var selectedQuestionsCount = {
        level_1_count: parseInt(current.getValue('level_1_count') || 0),
        level_2_count: parseInt(current.getValue('level_2_count') || 0),
        level_3_count: parseInt(current.getValue('level_3_count') || 0),
        level_4_count: parseInt(current.getValue('level_4_count') || 0)
    };

    for (var index = 1; index <= 4; index++) {
        var countFieldName = 'level_' + index + '_count';
        var currentLevelQuestions = [];

        questionGr = new GlideRecord('x_93700_arena_arena_question');
        questionGr.setLimit(selectedQuestionsCount[countFieldName]);
        questionGr.addActiveQuery();
        questionGr.addEncodedQuery(current.filter);
        questionGr.addQuery('level', 'level' + index);
        questionGr.query();
        while (questionGr.next()) {
            currentLevelQuestions.push(questionGr.getValue('sys_id'));
        }

        current.setValue(countFieldName, currentLevelQuestions.length);
        questions = questions.concat(currentLevelQuestions);
    }
}
current.setValue('questions', questions);
current.update();
action.setRedirectURL(current);
