sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var challenged_player = request.queryParams.challenged_player || '';
	var level = request.queryParams.level || '';
	var difficulty = request.queryParams.difficulty || '';
	var success = false;
	var challengedUser = '';

    var result = new GlideRecord("x_93700_arena_results");
    result.setLimit(1);
    result.addQuery("arena_user", challenged_player);
    result.addQuery('difficulty', difficulty); //the active difficulty
    result.addQuery('level', level); //the active level
    result.addQuery('active', true);
    result.query();
    if (result.next()) {
        var arenaUserGr = new GlideRecord('x_93700_arena_user');
        arenaUserGr.get('user', gs.getUserID());
        var userID = arenaUserGr.getValue('sys_id');
        result.challenged_by = userID;
        result.update();
		challengedUser = result.arena_user.avatar.toString() || result.arena_user.user.getDisplayValue();
		success = true;
    }
	if(success === true) { 
		response.setStatus(200); 
		response.setBody({ message: "You've challenged "+challengedUser });
	}
	else throw 'Invalid parameters';
})(request, response);
