sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var level = request.body.data.level;
    var difficulty = request.body.data.difficulty;
    var responses = request.body.data.answers;

    var resultId = new Evaluator().getResult(responses, false, level, difficulty);
    if (resultId && resultId !== null && resultId !== undefined && resultId !== '') {
        var resultGr = new GlideRecord('x_93700_arena_results');
        resultGr.get(resultId);

		var responseBody = {
			score: resultGr.getValue('total_score'),
			timeTaken: resultGr.getValue('time_taken'),
			nextLevel: null,
			nextDifficulty: null
		};

		var nextLevelData = getNextLevelData(level, difficulty);
		responseBody.nextLevel = nextLevelData.level;
		responseBody.nextDifficulty = nextLevelData.difficulty;

        response.setBody(responseBody);
        response.setStatus(200);
    } else {
        throw 'Invalid request';
    }

	function getNextLevelData(level, difficulty) {
		var nextLevel = null, nextDifficulty = null;
		var levelLabels = ['Level 1', 'Level 2', 'Level 3', 'Level 4'];
		var difficultyLabels = ['Easy', 'Medium', 'Hard'];

		var userGr = new GlideRecord('x_93700_arena_user');
		userGr.get('user', gs.getUserID());

		var levelChoices = userGr.getElement('level').getChoices();
		var difficultyChoices = userGr.getElement('difficulty').getChoices();

		var newUserLevelIndex = levelChoices.indexOf(userGr.getValue('level'));
		var newUserDifficultyIndex = difficultyChoices.indexOf(userGr.getValue('difficulty'));

		var currentLevelIndex = levelChoices.indexOf(level);
		var currentDifficultyIndex = difficultyChoices.indexOf(difficulty);

		if(newUserLevelIndex > currentLevelIndex || newUserDifficultyIndex > currentDifficultyIndex) {
			if(difficultyChoices.length === (currentDifficultyIndex+1)) {
				nextLevel = {label: levelLabels[currentLevelIndex+1], value: levelChoices[currentLevelIndex+1]};
				nextDifficulty = {label: difficultyLabels[0], value: difficultyChoices[0]};
			}
			else{
				nextLevel = {label: levelLabels[currentLevelIndex], value: levelChoices[currentLevelIndex]};
				nextDifficulty = {label: difficultyLabels[currentDifficultyIndex+1], value: difficultyChoices[currentDifficultyIndex+1]};
			}
		}

		return {
			level: nextLevel,
			difficulty: nextDifficulty
		};
	}

})(request, response);
