sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var currentUserID = gs.getUserID();
	var playerDetails = {};

	var challenges = [];
	var resultGr = new GlideRecord('x_93700_arena_results');
	resultGr.addQuery('arena_user.user', currentUserID);
	resultGr.addActiveQuery();
	resultGr.addNotNullQuery('challenged_by');
	resultGr.query();
	while(resultGr.next()) {
		var challenge = {};
		var challengedByName = resultGr.challenged_by.avatar.toString() || resultGr.challenged_by.user.getDisplayValue();
		var challengedByAvatar = resultGr.challenged_by.profile_image.getDisplayValue() || resultGr.challenged_by.user.photo.getDisplayValue() || '';
		var challengedByInitials = getInitials(challengedByName);
		challenge.level = {label: resultGr.getDisplayValue('level'), value: resultGr.getValue('level')};
		challenge.difficulty = {label: resultGr.getDisplayValue('difficulty'), value: resultGr.getValue('difficulty')};
		challenge.challenged_by = {label: challengedByName, value: resultGr.getValue('challenged_by'), avatar_src: challengedByAvatar, initials: challengedByInitials };
		challenges.push(challenge);
	}

	var arenaPlayerGr = new GlideRecord('x_93700_arena_user');
	arenaPlayerGr.addQuery('user', currentUserID);
	arenaPlayerGr.query();
	if(arenaPlayerGr.next()) {
		playerDetails.nickname = arenaPlayerGr.getValue('avatar') || arenaPlayerGr.user.getDisplayValue() || '';
		playerDetails.image = arenaPlayerGr.profile_image.getDisplayValue() || arenaPlayerGr.user.photo.getDisplayValue();
		playerDetails.id = arenaPlayerGr.getValue('sys_id');
		playerDetails.levelIndex = arenaPlayerGr.getElement('level').getChoices().indexOf(arenaPlayerGr.getValue('level'));
		playerDetails.difficultyIndex = arenaPlayerGr.getElement('difficulty').getChoices().indexOf(arenaPlayerGr.getValue('difficulty'));
		playerDetails.sfx = arenaPlayerGr.getValue('sfx') === '1';
		playerDetails.music = arenaPlayerGr.getValue('music') === '1';
		playerDetails.challenges = challenges;
		playerDetails.sfxSound = arenaPlayerGr.getValue('sfx_sounds');
		playerDetails.sfxChoices = arenaPlayerGr.getElement('sfx_sounds').getChoices();
		playerDetails.stats = [
			{label: 'Platform Administration', value: arenaPlayerGr.platform_excellence},
			{label: 'Scripting', value: arenaPlayerGr.scripting_excellence},
			{label: 'User Interface', value: arenaPlayerGr.ui_excellence},
			{label: 'Application', value: arenaPlayerGr.application_excellence}
		];
	}

	response.setBody(playerDetails);
	response.setStatus(200);

	function getInitials(name) {
		var first = (name.split(' ')[0] || '').charAt(0).toUpperCase();
		var last = (name.split(' ')[1] || '').charAt(0).toUpperCase();
		return first+last;
	}
})(request, response);
