sysauto_script.script:
var quotes = new GlideRecord('x_93700_arena_shankaran_pillai_quotes');
quotes.setLimit(1);
quotes.orderBy('sys_updated_on');
quotes.addQuery('quote_used', false);
quotes.query();
if (quotes.next()) {
    gs.setProperty('x_93700_arena.shankaran_pillai_quote', quotes.quote.toString());
    quotes.quote_used = true;
    quotes.update();
}

var usedQuotesCount = 0;
var totalQuotesCount = 0;
quotes = new GlideRecord('x_93700_arena_shankaran_pillai_quotes');
quotes.query();
while (quotes.next()) {
    if (quotes.quote_used == true) usedQuotesCount++;
	totalQuotesCount++;
}

if (usedQuotesCount === totalQuotesCount) {
    quotes = new GlideRecord('x_93700_arena_shankaran_pillai_quotes');
    quotes.setValue('quote_used', false);
    quotes.updateMultiple();
}
