sysauto_script.script:
//** Get each arena user and check when was the record created in result table  **//

var getArenaUser = new GlideRecord('x_93700_arena_user');
getArenaUser.query();
while (getArenaUser.next()) {
    var createdOn = '';
    var getLastCreate = new GlideRecord('x_93700_arena_results');
    getLastCreate.addQuery('arena_user', getArenaUser.getUniqueValue());
    getLastCreate.orderByDesc('sys_created_on'); //Get latest created record
    getLastCreate.setLimit(1);
    getLastCreate.query();
    if (getLastCreate.next()) {
        var gdt = new GlideDateTime(getLastCreate.sys_created_on);
		gdt.getDisplayValue();
        var curDate = new GlideDateTime();
		curDate.getDisplayValue();
        var diffSeconds = GlideDateTime.subtract(gdt, curDate);
        var getDays = diffSeconds.getDisplayValue().split(' ');
        if (getDays[1] == 'Hours') {
            getArenaUser.last_played_on = 'Yesterday';
        } else {
            getArenaUser.last_played_on = getDays[0] + ' ' + getDays[1] + ' ' + 'Ago'; //Update field in sample format "2 Days Ago"
        }

    }

    getArenaUser.update();
}
